import { useState } from 'react';

const YourComponent = () => {
  const [taskIdToDelete, setTaskIdToDelete] = useState(null);

  const handleDelete = (id) => {
    // Set the task ID to delete and open the modal
    setTaskIdToDelete(id);
    const modal = new bootstrap.Modal(document.getElementById('exampleModal'));
    modal.show();
  };

  const handleConfirmDelete = () => {
    if (taskIdToDelete !== null) {
      axios
        .delete(`http://localhost:3001/api/v1/tasks/${taskIdToDelete}`)
        .then(() => {
          getTasks();
          console.log("Task deleted!!");
        })
        .catch((error) => {
          console.log("Delete error:", error);
        })
        .finally(() => {
          // Reset the task ID to delete after handling the delete operation
          setTaskIdToDelete(null);
        });
    }
  };

  return (
    <div>
      {/* Your other content */}

      {/* Bootstrap modal for delete confirmation */}
      <div
        className="modal fade"
        id="exampleModal"
        tabIndex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">
                Delete
              </h1>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              Are you sure? This will delete the task permanently.
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleConfirmDelete}
              >
                Yes
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default YourComponent;
